document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".foldable .foldable-header").forEach(e=>{e.addEventListener("click",t=>{t.target.closest(".foldable").querySelector(".foldable-body").classList.toggle("unfolded")})}),document.querySelectorAll(".modal").forEach(e=>{e.addEventListener("click",t=>{t.target.classList.contains("modal")&&t.target.classList.add("hide")})}),document.querySelectorAll(".modal-trigger").forEach(e=>{e.addEventListener("click",t=>{const{target:l}=t.target.dataset,a=document.querySelector(l);a==null||a.classList.remove("hide")})}),document.querySelectorAll(".tabs").forEach(e=>{const t=document.querySelector(e.dataset.list);e.querySelectorAll(".tab-item").forEach(l=>{l.addEventListener("click",function(a){var o;e.querySelectorAll(".tab-item").forEach(c=>c.classList.remove("active")),t.querySelectorAll(".tab").forEach(c=>c.classList.add("hide")),(o=t.querySelector(`.tab${l.dataset.target}`))==null||o.classList.remove("hide"),l.classList.add("active")})})})});
