document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".foldable .foldable-header").forEach(t=>{t.addEventListener("click",e=>{e.target.closest(".foldable").querySelector(".foldable-body").classList.toggle("unfolded")})}),document.querySelectorAll(".modal").forEach(t=>{t.addEventListener("click",e=>{e.preventDefault(),e.target.classList.contains("modal")&&e.target.classList.add("hide")}),t.querySelectorAll(".content").forEach(e=>{e.addEventListener("click",l=>l.stopPropagation())})}),document.querySelectorAll(".modal-trigger").forEach(t=>{t.addEventListener("click",e=>{e.preventDefault();const{target:l}=e.target.dataset,o=document.querySelector(l);o==null||o.classList.remove("hide")})})});
